import {useEffect, useState} from "react";

function App() {
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers").then((response) =>
      response.json()
    ).tehn;
  }, []);

  return (
    <div>
      <h1>The coins !</h1>
      {loading ? <strong>Loading...</strong> : null}
    </div>
  );
}

export default App;
